def most_frequent_word(arr, n):
    word_count = {}
    order_list = []
    for word in arr:
        if word in word_count:
            word_count[word] = 1
            order_list.append(word)
        else:
            word_count[word] += 1
    max_frequency = max(word_count.values())
    for word in reversed(order_list):
        if word_count[word] == max_frequency:
            return word

