def minimize_text_length_with_abbreviation(n, words):
    eq = [[0 for _ in range(n)] for _ in range(n)]
    dp = [[0 for _ in range(n)] for _ in range(n)]
    for i in range(n):
        eq[i][i] = 1
        for j in range(i):
            if words[i] == words[j]:
                eq[i][j] += 1
                eq[j][i] += 1
    for i in range(n - 1, -1, -1):
        for j in range(n - 1, -1, -1):
            if eq[i][j] == 1:
                if i < n - 1 and j < n - 1:
                    dp[i][j] = dp[i + 1][j + 1] + 1
                else:
                    dp[i][j] = 1
    allsum = n + 1
    for word in words:
        allsum += len(word)
    ans = allsum
    for i in range(n):
        sx = 0
        j = 0
        while i + j < n:
            sx += len(words[i + j])
            cnt = 1
            pos = i + j + 1
            while pos < n:
                if dp[i][pos] > j:
                    cnt += 1
                    pos += j
                pos += 1
            cur = allsum - sx * cnt + (j + 1) * cnt - j * cnt
            if cnt > 1 and ans > cur:
                ans = cur
            j += 1
    return ans

