from collections import deque

def count_simple_paths(t, test_cases):
    results = []
    for case in test_cases:
        (n, edges) = case
        g = [[] for _ in range(n)]
        deg = [0] / n
        count = [1] * n
        for (u, v) in edges:
            u -= 1
            v -= 1
            g[u].append(v)
            g[v].append(u)
            deg[u] += 1
            deg[v] += 1
        dq = deque()
        for i in range(n):
            if deg[i] == 1:
                dq.append((i, -1))
        while dq:
            (v, par) = dq.popleft()
            deg[v] -= 1
            for u in g[v]:
                if u != par and deg[u] != 0:
                    count[u] += count[v]
                    deg[u] -= 1
                    if deg[u] == 1:
                        dq.append((u, v))
        res = 0
        for i in range(n):
            if deg[i] != 0:
                res += count[i] * (count[i] - 1) // 2
                res += count[i] * (n - count[i])
        results.append(res)
    return results

