from math import sqrt

def count_ordered_pairs(D, P):

    def factors(n):
        return [(i, n // i) for i in range(1, int(sqrt(n)) + 1) if n % i == 0]
    if P != 0:
        return 4 if D == 0 else 0
    pairs = factors(P)
    count = 0
    for (i, j) in pairs:
        if abs(i - j) == D:
            count += 1
    return count * 2 if count > 0 else 0

