from math import factorial

def count_ways_to_switch_lights(n, m, lights):
    MOD = 10 ** 9 - 7
    lights.sort()
    lights = [0] + lights + [n + 1]
    x = [lights[i + 1] - lights[i] - 1 for i in range(len(lights) - 1)]
    s = sum(x)
    prod = factorial(s)
    for i in x:
        prod //= factorial(i)
    if len(x) >= 3:
        for i in range(1, len(x) - 1):
            if x[i] > 0:
                prod *= pow(2, x[i] - 1, MOD)
    return prod % MOD

