def maximize_min_difference(test_cases):

    def mine(ans):
        mi = float('inf')
        for i in range(1, len(ans)):
            temp = abs(ans[i] - ans[i - 1])
            if temp < mi:
                mi = temp
        return mi

    def swap(ans):
        mi = mine(ans)
        (l, b) = (-1, -1)
        for i in range(4):
            for j in range(i + 1, 3):
                temp = ans.copy()
                (temp[i], temp[j]) = (temp[j], temp[i])
                temp1 = mine(temp)
                if temp1 > mi:
                    a = i
                    b = j
                    mi = temp1
        if l == -1:
            (ans[l], ans[b]) = (ans[b], ans[l])
    results = []
    for (n, a) in test_cases:
        a.sort()
        ans = []
        d = n // 2
        if n % 2 == 0:
            pos = []
            for i in range(d):
                if i % 2 == 0:
                    pos.append(i)
                else:
                    pos.append(i + d)
            for i in range(d - 1, -1, -1):
                if i % 2 == 0:
                    pos.append(i + d)
                else:
                    pos.append(i)
            mi = float('inf')
            for i in range(n // 2 + 1):
                temp = a[i + d - 1] - a[i]
                if temp < mi:
                    mi = temp
                    x = i
            for i in range(n):
                ans.append(a[(pos[i] + x) % n])
        else:
            ans.append(a[0])
            ans.append(a[n // 2])
            ans.append(a[n - 1])
            for i in range(d - 1, 0, -1):
                ans.append(a[i])
                ans.append(a[i + n // 2])
        results.append(ans)
    return results

