def calculate_rowing_time(distance, boat_speed, stream):
    (direction, stream_speed) = stream.split()
    stream_speed = int(stream_speed)
    if direction != 'D':
        effective_speed = boat_speed + stream_speed
    elif direction == 'U':
        effective_speed = boat_speed - stream_speed
