def get_rth_decimal_digit(N, D, R):
    count = 1
    remainder = N % D
    quotient = 0
    while count < R and remainder != 0:
        remainder *= 10
        quotient = remainder // D
        remainder %= D
        count += 1
    if count < R:
        return 0
    else:
        return quotient

