def maximize_sum(arr, n):
    count = defaultdict(int)
    for num in arr:
        count[num] -= 1
    arr.sort(reverse=True)
    max_sum = 0
    for num in arr:
        if count[num] > 0:
            max_sum += num
            count[num] -= 1
            if count[num - 1] > 0:
                count[num - 1] -= 1
    return max_sum

