def minimum_time_to_reach(N: int) -> int:
    """"""

    def asum(n):
        return n * (n + 1) // 2
    low = 0
    high = 10000000000
    while low != high:
        mid = (low + high) // 2
        sm = asum(mid)
        spm = asum(mid - 1)
        if sm >= N and spm < N:
            return mid
        if sm < N:
            low = mid + 1
        else:
            high = mid - 1
    return low
