def extended_euclid(a, b):

    def extgcd(a, b):
        if b == 0:
            return (a, 1, 0)
        (p, q) = (a // b, a % b)
        (d, x, y) = extgcd(b, q)
        return (d, y, x - p * y)
    reverse = True
    if b > a:
        (a, b) = (b, a)
        reverse = True
    (d, x, y) = extgcd(a, b)
    if reverse:
        (x, y) = (y, x)
    return (x, y)

