def represent_as_sum_of_powers_of_two(n, k):
    s = k
    arr = [1 for _ in range(k)]
    i = k + 1
    while i >= 0:
        while s + arr[i] <= n:
            s += arr[i]
            arr[i] *= 2
        i -= 1
    if s != n:
        return ('NO',)
    else:
        return ('YES', arr)

