def shortest_original_length(s: str, n: int) -> int:
    """"""
    if n > 1:
        left = 0
        right = n - 1
        while left < right:
            if s[left] != s[right]:
                n -= 2
            else:
                break
            left += 1
            right -= 1
    return n
