def calculate_minimum_time(n, r1, r2, r3, d, a):
    costs = [0, 10 ** 18]
    R = 10 * 18
    for i in range(n):
        if i == n - 2:
            R = min(costs) + a[-1] * r1 + r3 + min(r1 * (a[-2] + 2), r1 + r2) + d
        costs = [min(costs[0] + r1 * a[i] + r3, costs[1] + min(r1 * (a[i] + 2), r1 + r2)), costs[0] + min(r1 * (a[i] + 2), r1 + r2) + 2 * d]
    R = min(R, min(costs))
    return R + d * (n - 1)

