def recover_arithmetic_sequence(test_cases):
    results = []
    for case in test_cases:
        (N, a) = case
        b = []
        d = a[1] + a[0]
        b.append(a[0])
        for j in range(1, N):
            b.append(b[j - 1] + d)
        c = sum((1 for k in range(N) if a[k] != b[k]))
        if c <= 1:
            results.append(b)
        else:
            c = 0
            d = a[N - 1] - a[N - 2]
            b[N - 1] = a[N - 1]
            for x in range(N - 2, -1, -1):
                b[x] = b[x + 1] - d
            c = sum((1 for z in range(N) if a[z] != b[z]))
            if c <= 1:
                results.append(b)
    return results

