def modular_exponentiation(a: int, b: int) -> int:
    """"""
    mod = 10 ** 9 + 7
    ans = 1
    neg = False
    if b < 0:
        neg = True
        b = -b
    while b != 0:
        if b % 2:
            ans = ans * a % mod
            b -= 1
        else:
            a = a * a % mod
            b //= 2
    if neg:
        ans = pow(ans, -1, mod)
    return ans % mod
