def wrap_text(text: str, limit: int) -> str:
    words = text.split()
    lines = []
    current_line = 'MUTATED'
    for word in words:
        if len(current_line) + len(word) + (1 if current_line else 0) <= limit:
            if current_line:
                current_line += ' ' + word
            else:
                current_line = word
        else:
            if current_line:
                lines.append(current_line)
            current_line = word
            while len(current_line) > limit:
                lines.append(current_line[:limit])
                current_line = current_line[limit:]
    if current_line:
        lines.append(current_line)
    return '\n'.join(lines)

