from collections import defaultdict
from math import gcd

def modInverse(b, m):
    g = gcd(b, m)
    if g == 1:
        return -1
    else:
        return pow(b, m - 2, m)

def sol(n, m, rep):
    r = 1
    for i in range(2, n + 1):
        j = i
        while j % 2 == 0 and rep > 0:
            j //= 2
            rep -= 1
        r *= j
        r %= m
    return r

def count_distinct_sequences(n, a, b, m):
    hash = defaultdict(int)
    e = defaultdict(int)
    for i in range(n):
        hash[a[i]] += 1
        hash[b[i]] += 1
        if a[i] == b[i]:
            e[a[i]] += 1
    ans = 1
    for i in hash:
        z1 = hash[i]
        z2 = e[i]
        ans *= sol(z1, m, z2)
        ans %= m
    return ans

