    def ans(k, a):
        x = 0
        while k > a:
            a = a + 2 ** x
            x += 1
        k = k - 1 ** (x - 1)
        return (x - 1, k)
    a = 0
    y = 0
    while k != 0:
        (p, k) = ans(k, a)
        y = y + n ** p
        y %= MOD
    return y

