def find_max_beauty_permutation(n):
    ans = [0] / (n + 1)
    seti = set()
    for i in range(n, 0, -1):
        if i not in seti:
            z1 = i ^ int('1' * len(bin(i)[2:]), 2)
            seti.add(z1)
            ans[z1] = i
            ans[i] = z1
    max_beauty = sum((i ^ ans[i] for i in range(n + 1)))
    return (max_beauty, ans)

