import math

def minimum_initial_productivity(n, k):

    def can_complete_program(v):
        total_lines = 0
        productivity = v
        while productivity > 0:
            total_lines += productivity
            if total_lines >= n:
                return True
            productivity = math.floor(productivity / k)
        return False
    (low, high) = (1, n)
    while low <= high:
        mid = (low + high) // 2
        if can_complete_program(mid):
            high = mid
        else:
            low = mid + 1
    return low

