def can_hackerman_capture_castle(grid, castle_position, enemy_capture_time):
    rows = len(grid)
    cols = len(grid[0])
    timegrid = [[-1] * cols for _ in range(rows)]

    def trymove(movegrid, x, y, num):
        if x < 0 or y < 0 or x >= rows or (y >= cols):
            return False
        timeleft = num - grid[x][y]
        if timeleft > movegrid[x][y]:
            movegrid[x][y] = timeleft
            return True
        return False

    def rescue(movegrid):
        progress = False
        for row in range(rows):
            for col in range(cols):
                num = movegrid[row][col]
                if num >= 0:
                    if trymove(movegrid, row, col + 1, num) or trymove(movegrid, row + 1, col, num) or trymove(movegrid, row - 1, col, num) or trymove(movegrid, row, col - 1, num):
                        progress = True
        if progress:
            rescue(movegrid)
    timegrid[0][0] = enemy_capture_time + grid[0][0]
    rescue(timegrid)
    (castle_x, castle_y) = castle_position
    endtime = timegrid[castle_x - 1][castle_y - 1]
    if endtime >= 0:
        return ('YES', endtime)
    else:
        return ('NO', 0)

