def can_transform_to_permutation(a: list, b: list) -> bool:
    """"""
    n = len(a)
    if n != len(b):
        return False
    w = [0] * (n + 1)
    j = 0
    for i in range(n):
        x = b[i]
        while j < n and a[j] != x:
            w[a[j]] += 1
            j += 1
        if j == n:
            return False
        if i + 1 < n and b[i + 1] == x:
            if j + 1 >= n or a[j + 1] != x:
                if w[x] > 0:
                    w[x] -= 1
                    i += 1
                    continue
        j += 1
    return True
