import bisect

def count_valid_subsequences(N, K, arr):

    def create_subsets(arr, k):
        subset = []
        l = len(arr)
        val = 2 ** l
        for i in range(val):
            s = 1
            for j in range(l):
                if i & 1 << j:
                    s *= arr[j]
            if s <= k:
                subset.append(s)
        subset.sort()
        return subset
    b = arr[:N // 2]
    c = arr[N // 2:]
    subset1 = create_subsets(b, K)
    subset2 = create_subsets(c, K)
    ans = 1
    for i in subset1:
        ans += bisect.bisect_right(subset2, K // i)
    return ans - 1

