def calculate_max_popularity(test_cases):

    def comp(p1, p2):
        if p1[0] != p2[0]:
            return p2[1] - p1[1]
        else:
            return p1[0] - p2[0]
    results = []
    for theories in test_cases:
        B = []
        for (T1, T2) in theories:
            B.append((T1, 0))
            B.append((T2, 1))
        B = sorted(B, key=lambda x: (x[0], x[1]))
        C = [0] * len(B)
        C[0] = 1
        maxx = -float('inf')
        for i in range(1, len(B)):
            if B[i][1] == 0:
                C[i] = C[i - 1] + 1
                maxx = max(maxx, C[i])
            else:
                C[i] = C[i - 1] - 1
        results.append(maxx)
    return results

