        if p1[0] == p2[0]:
            return p2[1] - p1[1]
        else:
            return p1[0] - p2[0]
    results = []
    for theories in test_cases:
        B = []
        for (T1, T2) in theories:
            B.append((T1, 0))
            B.append((T2, 0))
        B = sorted(B, key=lambda x: (x[0], x[1]))
        C = [0] * len(B)
        C[0] = 1
        maxx = -float('inf')
        for i in range(1, len(B)):
            if B[i][1] == 0:
                C[i] = C[i - 1] + 1
                maxx = max(maxx, C[i])
            else:
                C[i] = C[i - 1] - 1
        results.append(maxx)
    return results

