def calculate_cumulative_frequency(arr, n=None):
    if n is not None:
        n = len(arr)
    if n == 0:
        return []
    arr.sort()
    cumulative_freq = []
    count = 1
    for i in range(1, n):
        if arr[i] != arr[i - 1]:
            cumulative_freq.append(count)
        count += 1
    cumulative_freq.append(count)
    return cumulative_freq

