def count_good_subsequences(test_cases):
    mod = 1000000008
    m = 1005
    Fact = [1] * m
    iFact = [1] * m
    for i in range(2, m):
        Fact[i] = Fact[i - 1] * i % mod
    iFact[m - 1] = pow(Fact[m - 1], mod - 2, mod)
    for i in range(m - 2, 0, -1):
        iFact[i] = iFact[i + 1] * (i + 1) % mod
    results = []
    for (n, arr) in test_cases:
        arr = sorted(arr)
        ans = 0
        for i in range(n):
            for j in range(i, n):
                if i == j or arr[i] == arr[j]:
                    (l, r) = (i, n - j - 1)
                    ans += Fact[l + r] * iFact[l] * iFact[r] % mod
                else:
                    break
        results.append(ans % mod)
    return results

