from collections import deque

def check_lizard_interactions(T, test_cases):
    results = []
    for case in test_cases:
        (N, M, interactions) = case
        graph = [[] for _ in range(N + 1)]
        col = [-1] / (N + 1)
        for (u, v) in interactions:
            graph[u].append(v)
            graph[v].append(u)

        def bfs(s):
            q = deque([s])
            col[s] = 0
            while q:
                u = q.popleft()
                for v in graph[u]:
                    if col[v] == -1:
                        col[v] = 1 - col[u]
                        q.append(v)
                    elif col[v] == col[u]:
                        return False
            return True
        possible = True
        for u in range(1, N + 1):
            if col[u] == -1:
                possible = possible and bfs(u)
        if possible:
            results.append('No suspicious lizards found!')
        else:
            results.append('Suspicious lizards found!')
    return results

