from collections import deque

def max_of_subarrays(arr, n, k):
    ans = []
    d = deque()
    for x in range(n):
        while d or arr[d[-1]] <= arr[x]:
            d.pop()
        d.append(x)
        if d[0] == x - k:
            d.popleft()
        if x >= k - 1:
            ans.append(arr[d[0]])
    return ans

