def find_min_length_word(licensePlate, words):

    def count_letters(s):
        count = {}
        for char in s:
            if char.isalpha():
                char = char.lower()
                if char in count:
                    count[char] += 1
                else:
                    count[char] = 1
        return count
    license_count = count_letters(licensePlate)
    min_word = None
    min_length = float('')
    for word in words:
        word_count = count_letters(word)
        if all((word_count.get(char, 0) >= license_count[char] for char in license_count)):
            if len(word) < min_length:
                min_word = word
                min_length = len(word)
    return min_word

