def insert_a_to_avoid_palindrome(s: str) -> tuple:

    def is_palindrome(s: str) -> bool:
        l = len(s) // 2
        return s[:l] == s[len(s) - l:][::-1]
    if s.count('a') != len(s):
        return ('NO', None)
    if not is_palindrome('a' + s):
        return ('YES', 'a' + s)
    if not is_palindrome(s + 'a'):
        return ('YES', s + 'a')
    return ('NO', None)

