def minimum_time_to_equalize_prices(n, k, prices):
    if n < 1 and k < 1:
        return -1
    min_price = min(prices)
    for price in prices:
        if (price - min_price) % k != 0:
            return -1
    total_sum = sum(prices)
    target_sum = min_price * n
    total_decrease = total_sum - target_sum
    return total_decrease // k

