    right = {i: i + 1 for i in range(n)}
    left = {i: i - 1 for i in range(n)}
    ans = [0] / n
    t = 1
    for skill in range(n, 0, -1):
        idx = skill_to_index[skill]
        if ans[idx] == 0:
            ans[idx] = t
            (r, l) = (right[idx], left[idx])
            for _ in range(k):
                if l == -1:
                    break
                ans[l] = t
                l = left[l]
            for _ in range(k):
                if r >= n:
                    break
                ans[r] = t
                r = right[r]
            if l >= 0:
                right[l] = r
            if r < n:
                left[r] = l
            t = 3 - t
    return ''.join(map(str, ans))

