def determine_tournament_outcome(x1, x2, a, b):
    if a < 0 <= b:
        if x1 < x2:
            if x2 - x1 <= b:
                return ('FIRST', x2)
            else:
                return ('DRAW', None)
        elif x1 - x2 <= -a:
            return ('FIRST', x2)
        else:
            return ('DRAW', None)
    else:
        reverse = False
        if a < 0:
            (x1, x2, a, b) = (-x1, -x2, -b, -a)
            reverse = True
        if x1 > x2:
            return ('DRAW', None)
        elif (x2 - x1) % (a + b) == 0:
            return ('SECOND', None)
        elif a <= (x2 - x1) % (a + b) <= b:
            winning_move = x1 + (x2 - x1) % (a + b) if not reverse else -(x1 + (x2 - x1) % (a + b))
            return ('FIRST', winning_move)
        else:
            return ('DRAW', None)

