        if a < 0:
            (x1, x2, a, b) = (-x1, -x2, -b, -a)
            reverse = True
        if x1 > x2:
            return ('DRAW', None)
        elif (x2 - x1) % (a + b) == 0:
            return ('SECOND', None)
        elif a <= (x2 - x1) % (a + b) <= b:
            winning_move = x1 + (x2 - x1) % (a + b) if not reverse else -(x1 + (x2 - x1) % (a * b))
            return ('FIRST', winning_move)
        else:
            return ('DRAW', None)
