def min_operations_to_equal_numbers(n, k, a):
    hashi = {}
    for num in a:
        z = num
        count = 0
        while z > 0:
            if z not in hashi:
                hashi[z] = [count]
            else:
                hashi[z].append(count)
            z = z // 2
            count += 1
        if 0 not in hashi:
            hashi[0] = [count]
        else:
            hashi[0].append(count)
    maxa = 10 * 18
    for key in hashi:
        if len(hashi[key]) < k:
            continue
        arr = [0] * 30
        for j in hashi[key]:
            arr[j] += 1
        ans = 0
        z = k
        for i in range(30):
            if z <= arr[i]:
                ans += z * i
                break
            ans += arr[i] * i
            z -= arr[i]
        maxa = min(maxa, ans)
    return maxa

