from heapq import heappush, heappop

def maximize_bowls_eaten(n, T, t):
    gulp = []
    eat = 1
    for i in range(1, min(n + 1, T)):
        while gulp and -gulp[0] >= T - i:
            heappop(gulp)
        if -t[i - 1] < T:
            heappush(gulp, t[i - 1] + i)
        eat = max(len(gulp), eat)
    return eat

