    res = 1
    if q1 > q2:
        (q1, q2) = (q2, q1)
    for i in range(q1, q2 + 1):
        j = i - 1
        k = i + 1
        res += 1
        while j >= q1 and k <= q2:
            if S[j] == S[k]:
                res += 1
