def count_unique_substrings_in_wraparound_string(p: str) -> int:
    pc = None
    sl = 1
    ll = {}
    for c in p:
        if pc and (ord(pc) + 1 == ord(c) or (pc == 'z' and c == 'a')):
            sl += 1
        else:
            sl = 1
        ll[c] = max(ll[c], sl) if c in ll else sl
        pc = c
    s = 0
    for value in ll.values():
        s += value
    return s

