def manage_flags(command, i=None, bit_flag=1):
    BIT_MASK = (1 << 64) - 1
    if command == '0':
        return 1 if bit_flag & 1 << i else 0
    elif command == '1':
        return bit_flag | 1 << i
    elif command == '2':
        return bit_flag & ~(1 << i)
    elif command == '3':
        return bit_flag ^ 1 << i
    elif command == '4':
        return 1 if not ~bit_flag & BIT_MASK else 0
    elif command == '5':
        return 1 if bit_flag else 0
    elif command == '6':
        return 1 if not bit_flag else 0
    elif command == '7':
        return bin(bit_flag).count('1')
    elif command == '8':
        return bit_flag
    else:
        raise ValueError('Invalid command')

