def is_prime(a):
    if a <= 2:
        return False
    if a == 2 or a == 3:
        return True
    if a % 2 == 0 or a % 3 == 0:
        return False
    max_divisor = int(a ** 0.5)
    (d, i) = (5, 2)
    while d <= max_divisor:
        if a % d == 0:
            return False
        d += i
        i = 6 - i
    return True

def find_prime_neighbors(n):
    bef_prime = None
    aft_prime = None
    for num in range(n - 1, 1, -1):
        if is_prime(num):
            bef_prime = num
            break
    for num in range(n + 1, 3 * n):
        if is_prime(num):
            aft_prime = num
            break
    return [bef_prime, aft_prime]

