def perform_crazy_operation(N, T, A, P):
    """"""
    A = [0] + A
    P = [0] + P

    def apply_permutation(p, x):
        """Helper function to apply permutation p to array x."""
        res = [0] * len(p)
        for i in range(1, len(p)):
            res[i] = x[p[i]]
        return res
    while T > 0:
        if T % 2 == 1:
            A = apply_permutation(P, A)
        P = apply_permutation(P, P)
        T //= 2
    return A[1:]
