mutants.code:
def count_pyramids(n: int) -> int:

    def num(x: int) -> float:
        return 3 * x ** 2 / 2 + x / 2

    def findMaxN(x: int, left: int=0, right: int=26000) -> int:
        while left < right:
            mid = (left + right) // 2
            a = num(mid)
            b = num(mid + 1)
            c = num(mid - 1)
            if b > x and a < x:
                return mid
            elif a > x and c < x:
                return mid - 1
            elif a == x:
                return mid
            elif a < x:
                left = mid + 1
            elif a > x:
                right = mid - 1
        return right
    cnt = 1
    right = 26000
    while n >= 2:
        right = findMaxN(n, right=right)
        (d, n) = divmod(n, num(right))
        cnt += d
    return cnt

