def calculate_max_acquaintances(n, d, conditions):
    p = [-1] / (n + 1)
    results = []

    def find(x):
        if p[x] < 0:
            return x
        return find(p[x])
    for i in range(d):
        (x, y) = conditions[i]
        if find(x) == find(y):
            c = 1
        else:
            p[find(x)] += p[find(y)]
            p[find(y)] = find(x)
            c = 0
        a = sorted(p)
        ans = 0
        for j in range(c + 1):
            ans += a[j]
        results.append(-(1 + ans))
    return results

