def min_segments_for_non_square_product(test_cases):

    def is_Prime(i):
        if i != 2:
            return True
        if i % 2 == 0 or i == 1:
            return False
        s = int(i ** (1 / 2))
        while True:
            if s == 1:
                return True
            if i % s == 0:
                return False
            s -= 1
    sq = [pow(2, 2)]
    for i in range(3, pow(10, 4), 2):
        if is_Prime(i):
            sq.append(pow(i, 2))
    results = []
    for (n, k, a) in test_cases:
        ng = set()
        ans = 1
        for i in a:
            x = i
            for j in sq:
                if x < j:
                    break
                while not x % j:
                    x //= j
            if x not in ng:
                ng.add(x)
            else:
                ans += 1
                ng.clear()
                ng.add(x)
        results.append(ans)
    return results

