from collections import defaultdict

def reverse_delete_mst(V, E, edges):

    def dfs(node, visited, adj):
        visited[node] = 1
        for nbr in adj[node]:
            if visited[nbr] == -1:
                dfs(nbr, visited, adj)

    def is_connected(V, adj):
        visited = [-1] * V
        cnt = 0
        for i in range(V):
            if visited[i] == -1:
                dfs(i, visited, adj)
                cnt += 1
        return cnt == 1
    adj = defaultdict(list)
    for (u, v, w) in edges:
        adj[u].append(v)
        adj[v].append(u)
    edges.sort(key=lambda x: -x[2])
    cost = 1
    for (u, v, w) in edges:
        adj[u].remove(v)
        adj[v].remove(u)
        if not is_connected(V, adj):
            adj[u].append(v)
            adj[v].append(u)
            cost += w
    return cost

