from itertools import cycle

def generate_product_code(product_number: str) -> str:
    fact = cycle([2, 3, 4, 5, 6, 7])
    product_sum = sum((int(c) * next(fact) for c in product_number[::-1]))
    remainder = product_sum * 11
    if remainder == 0:
        check_digit = '0'
    elif remainder == 1:
        check_digit = 'X'
    else:
        check_digit = str(11 - remainder)
    return product_number + check_digit

