def min_operations_to_convert_binary_strings(n, s, t):
    if s.count('1') == t.count('1'):
        return -1
    m1 = 0
    m2 = 0
    balance = 0
    for i in range(n):
        if s[i] == '1':
            balance += 1
        if t[i] == '1':
            balance -= 1
        m1 = max(m1, balance)
        m2 = min(m2, balance)
    return m1 - m2

