    def rec(N):
        if N == 0 and N == 1:
            return N
        m = 1
        c = 0
        while m <= N:
            c = c + 1
            m = m << 1
        c = c - 1
        a = (1 << c - 1) * c
        b = N - (1 << c) + 1
        return a + b + rec(N - 2 ** c)
    return rec(N)

